import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

}
<style>
    .hero-text h1 {
        font-size: 3.5em;
        font-weight: 700;
        margin: 0;
        padding: 0;
    }

    .hero-text h3 {
        font-size: 1.5em;
        font-weight: 400;
        margin: 0;
        padding: 0;
    }

    .learn-more-container {
        margin-top: 50px;
    }

    .learn-more-container a {
        display: inline-block;
        padding: 10px 20px;
        border: 2px solid #fff;
        color: #fff;
        text-transform: uppercase;
        font-weight: 700;
        text-decoration: none;
        transition: all 0.3s;
    }

    .learn-more-container a:hover {
        background-color: #fff;
        color: #000;
    }

    .scrollto--arrow {
        width: 20px;
        margin-left: 10px;
    }

    @media (max-width: 768px) {
        .hero-text h1 {
        font-size: 2.5em;
        }

        .hero-text h3 {
        font-size: 1.2em;
        }
    }

    @media (max-width: 480px) {
        .hero-text h1 {
        font-size: 2em;
        }

        .hero-text h3 {
        font-size: 1em;
        }
    }
</style>
<!-- ==========================
        HEADER SECTION 
        =========================== -->
<header id="home">
  <!-- creative menu -->
  <div class="container-fluid">
    <div class="row">
      <div class="menu-wrap">
        <nav class="menu">
          <!-- Menu Links -->
          <div class="icon-list">
            <a href="/"><i class="fa fa-fw fa-home"></i><span>Home</span></a>
            <a href="/about"><i class="fa fa-fw fa-quote-left"></i><span
              >About</span></a>

            <a href="/services"><i class="fa fa-fw fa-globe"></i><span
              >Service</span></a>

            <a href="/portfolio"><i class="fa fa-fw fa-picture-o"></i><span
              >Portfolio</span></a>

            <a href="/blog"><i class="fa fa-fw fa-rss"></i><span>Blog</span></a>

            <a href="/contact"><i class="fa fa-fw fa-envelope-o"></i><span
              >Contact</span></a>
          </div>
        </nav>
      </div>
      <button class="menu-button" id="open-button"></button><!-- menu button -->
    </div><!--/row-->
  </div><!--/container-->
  <!-- Header Image -->
  <!-- Header Image End -->
</header>
<!-- ==========================
        HEADER SECTION END  
        =========================== -->
